import React, { useState, useEffect } from 'react';

const UDPComponent = ({ localPort, proxyHost, proxyPort }) => {
  const [message, setMessage] = useState('');
  const [receivedMessages, setReceivedMessages] = useState([]);
  const [statistics, setStatistics] = useState({ sent: 0, received: 0, lost: 0 });

  const socket = new WebSocket(`ws://${proxyHost}:${proxyPort}`);

  useEffect(() => {
    socket.addEventListener('message', (event) => {
      const data = JSON.parse(event.data);

      if (data.type === 'message') {
        setReceivedMessages((prevMessages) => [...prevMessages, data.content]);
        setStatistics((prevStats) => ({ ...prevStats, received: prevStats.received + 1 }));
      } else if (data.type === 'packetLoss') {
        setStatistics((prevStats) => ({ ...prevStats, lost: prevStats.lost + 1 }));
      }
    });

    return () => {
      socket.close();
    };
  }, [socket]);

  const sendMessage = () => {
    const packetLoss = Math.random() < 0.2;
    const data = JSON.stringify({
      type: 'message',
      content: message,
      packetLoss,
    });
    socket.send(data);
    setStatistics((prevStats) => ({ ...prevStats, sent: prevStats.sent + 1 }));
    setMessage('');
  };

  return (
    <div>
      <h2>UDP Component</h2>
      <p>Local Port: {localPort}</p>
      <input type="text" value={message} onChange={(e) => setMessage(e.target.value)} />
      <button onClick={sendMessage}>Send Message</button>
      <div>
        <h3>Received Messages:</h3>
        <ul>
          {receivedMessages.map((msg, index) => (
            <li key={index}>{msg}</li>
          ))}
        </ul>
      </div>
      <div>
        <h3>Statistics:</h3>
        <p>Sent: {statistics.sent}</p>
        <p>Received: {statistics.received}</p>
        <p>Lost: {statistics.lost}</p>
      </div>
    </div>
  );
};

export default UDPComponent;
