import React, { useState, useEffect } from 'react';

const UDPComponent = ({ localPort, proxyHost, proxyPort, direction }) => {
  const [messages, setMessages] = useState([]);
  const [sourceToTargetLoss, setSourceToTargetLoss] = useState(0.2);
  const [targetToSourceLoss, setTargetToSourceLoss] = useState(0.2);
  const [sourceDelay, setSourceDelay] = useState(0);
  const [targetDelay, setTargetDelay] = useState(0);

  const socket = new WebSocket(`ws://${proxyHost}:${proxyPort}`);

  useEffect(() => {
    socket.addEventListener('message', (event) => {
      const data = JSON.parse(event.data);

      if (data.type === 'message' && data.direction === direction) {
        setMessages((prevMessages) => [...prevMessages, data.content]);
      }
    });

    return () => {
      socket.close();
    };
  }, [socket, direction]);

  useEffect(() => {
    const sendSettings = () => {
      if (socket.readyState === WebSocket.OPEN) {
        const settings = {
          type: 'settings',
          direction,
          sourceToTargetLoss,
          targetToSourceLoss,
          sourceDelay,
          targetDelay,
        };
        socket.send(JSON.stringify(settings));
      }
    };

    sendSettings(); // Send settings on initial render

    // Send settings whenever they change
    const timeoutId = setTimeout(() => {
      sendSettings();
    }, 500);

    return () => clearTimeout(timeoutId);
  }, [socket, sourceToTargetLoss, targetToSourceLoss, sourceDelay, targetDelay, direction]);

  return (
    <div>
      <h2>{direction === 'source' ? 'Source' : 'Target'} Box (Local Port: {localPort})</h2>
      <div>
        <h3>Received Messages:</h3>
        <ul>
          {messages.map((msg, index) => (
            <li key={index}>{msg}</li>
          ))}
        </ul>
      </div>
      <div>
        <h3>Settings:</h3>
        <label>
          Source to Target Loss:
          <input
            type="number"
            value={sourceToTargetLoss}
            onChange={(e) => setSourceToTargetLoss(parseFloat(e.target.value))}
          />
        </label>
        <label>
          Target to Source Loss:
          <input
            type="number"
            value={targetToSourceLoss}
            onChange={(e) => setTargetToSourceLoss(parseFloat(e.target.value))}
          />
        </label>
        <label>
          Source Delay (ms):
          <input
            type="number"
            value={sourceDelay}
            onChange={(e) => setSourceDelay(parseInt(e.target.value))}
          />
        </label>
        <label>
          Target Delay (ms):
          <input
            type="number"
            value={targetDelay}
            onChange={(e) => setTargetDelay(parseInt(e.target.value))}
          />
        </label>
      </div>
    </div>
  );
};

export default UDPComponent;
