import React, { useState, useEffect } from 'react';

const UDPComponent = ({ localPort, proxyHost, proxyPort }) => {
  const [sourceMessages, setSourceMessages] = useState([]);
  const [targetMessages, setTargetMessages] = useState([]);
  const [sourceLoss, setSourceLoss] = useState(0.2); // Default loss for source
  const [targetLoss, setTargetLoss] = useState(0.2); // Default loss for target
  const [sourceDelay, setSourceDelay] = useState(0); // Default delay for source
  const [targetDelay, setTargetDelay] = useState(0); // Default delay for target

  const socket = new WebSocket(`ws://${proxyHost}:${proxyPort}`);

  useEffect(() => {
    socket.addEventListener('message', (event) => {
      const data = JSON.parse(event.data);

      if (data.type === 'message') {
        if (data.sourcePort === localPort) {
          setSourceMessages((prevMessages) => [...prevMessages, data.content]);
        } else {
          setTargetMessages((prevMessages) => [...prevMessages, data.content]);
        }
      }
    });

    return () => {
      socket.close();
    };
  }, [socket, localPort]);

  useEffect(() => {
    const sendSettings = () => {
      const settings = {
        type: 'settings',
        sourceLoss,
        targetLoss,
        sourceDelay,
        targetDelay,
      };
      socket.send(JSON.stringify(settings));
    };

    sendSettings(); // Send settings on initial render

    // Send settings whenever they change
    const timeoutId = setTimeout(() => {
      sendSettings();
    }, 500);

    return () => clearTimeout(timeoutId);
  }, [socket, sourceLoss, targetLoss, sourceDelay, targetDelay]);

  return (
    <div>
      <h2>Source Box (Local Port: {localPort})</h2>
      <div>
        <h3>Received Messages:</h3>
        <ul>
          {sourceMessages.map((msg, index) => (
            <li key={index}>{msg}</li>
          ))}
        </ul>
      </div>
      <h2>Target Box</h2>
      <div>
        <h3>Received Messages:</h3>
        <ul>
          {targetMessages.map((msg, index) => (
            <li key={index}>{msg}</li>
          ))}
        </ul>
      </div>
      <div>
        <h3>Settings:</h3>
        <label>
          Source Loss:
          <input
            type="number"
            value={sourceLoss}
            onChange={(e) => setSourceLoss(parseFloat(e.target.value))}
          />
        </label>
        <label>
          Target Loss:
          <input
            type="number"
            value={targetLoss}
            onChange={(e) => setTargetLoss(parseFloat(e.target.value))}
          />
        </label>
        <label>
          Source Delay (ms):
          <input
            type="number"
            value={sourceDelay}
            onChange={(e) => setSourceDelay(parseInt(e.target.value))}
          />
        </label>
        <label>
          Target Delay (ms):
          <input
            type="number"
            value={targetDelay}
            onChange={(e) => setTargetDelay(parseInt(e.target.value))}
          />
        </label>
      </div>
    </div>
  );
};

export default UDPComponent;
