// UDPComponent.js
import React, { useState, useEffect } from 'react';

const UDPComponent = ({ localPort, proxyHost, proxyPort, direction }) => {
  const [messages, setMessages] = useState([]);
  const [loss, setLoss] = useState(0.2); // Default loss
  const [delay, setDelay] = useState(0); // Default delay

  const [socket, setSocket] = useState(null);

  useEffect(() => {
    const newSocket = new WebSocket(`ws://${proxyHost}:${proxyPort}`);
    setSocket(newSocket);

    newSocket.addEventListener('message', (event) => {
      const data = JSON.parse(event.data);
      console.log('Received:', data); // Log incoming messages
      if (data.type === 'message' && data.direction === direction) {
        setMessages((prevMessages) => [...prevMessages, data.content]);
      }
    });

    return () => {
      newSocket.close();
    };
  }, [proxyHost, proxyPort, direction]);

  useEffect(() => {
    if (socket) {
      const sendSettings = () => {
        const settings = {
          type: 'settings',
          direction,
          loss,
          delay,
        };
        socket.send(JSON.stringify(settings));
      };

      sendSettings(); // Send settings on initial render

      // Send settings whenever they change
      const timeoutId = setTimeout(() => {
        sendSettings();
      }, 500);

      return () => clearTimeout(timeoutId);
    }
  }, [socket, loss, delay, direction]);

  return (
    <div>
      <h2>{direction === 'source' ? 'Source' : 'Target'} Box (Local Port: {localPort})</h2>
      <div>
        <h3>Received Messages:</h3>
        <ul>
          {messages.map((msg, index) => (
            <li key={index}>{msg}</li>
          ))}
        </ul>
      </div>
      <div>
        <h3>Settings:</h3>
        <label>
          Loss:
          <input type="number" value={loss} onChange={(e) => setLoss(parseFloat(e.target.value))} />
        </label>
        <label>
          Delay (ms):
          <input type="number" value={delay} onChange={(e) => setDelay(parseInt(e.target.value))} />
        </label>
      </div>
    </div>
  );
};

export default UDPComponent;
