import React, { useState } from 'react';
import UDPComponent from './Components/UDPComponent';
import './App.css';

const sharedWebSocket = new WebSocket('ws://localhost:60000');

const App = () => {
  const [targetIP, setTargetIP] = useState('127.0.0.1');
  const [targetPort, setTargetPort] = useState(50001);
  const [sourcePort, setSourcePort] = useState(50000);

  return (
    <div className="">
      <header>
        <header>
          <h1>Packet Checkpoint</h1>
        </header>
      </header>
      <div>
        <label>
          Target IP:
          <input type="text" value={targetIP} onChange={(e) => setTargetIP(e.target.value)} />
        </label>
        <label>
          Target Port:
          <input type="number" value={targetPort} onChange={(e) => setTargetPort(e.target.value)} />
        </label>
        <label>
          Source Port:
          <input type="number" value={sourcePort} onChange={(e) => setSourcePort(e.target.value)} />
        </label>
      </div>
      <UDPComponent localPort={sourcePort} direction="source" sharedWebSocket={sharedWebSocket} />
      <UDPComponent localPort={targetPort} direction="target" sharedWebSocket={sharedWebSocket} />
    </div>
  );
};

export default App;
