import React, { useState, useEffect } from 'react';

const UDPComponent = ({ localPort, proxyHost, proxyPort }) => {
  const [sourceMessage, setSourceMessage] = useState('');
  const [targetMessage, setTargetMessage] = useState('');
  const [receivedSourceMessages, setReceivedSourceMessages] = useState([]);
  const [receivedTargetMessages, setReceivedTargetMessages] = useState([]);
  const [statistics, setStatistics] = useState({ sourceSent: 0, targetSent: 0, received: 0, lost: 0 });

  const socket = new WebSocket(`ws://${proxyHost}:${proxyPort}`);

  useEffect(() => {
    socket.addEventListener('message', (event) => {
      const data = JSON.parse(event.data);

      if (data.type === 'message') {
        setStatistics((prevStats) => ({ ...prevStats, received: prevStats.received + 1 }));

        if (data.sourcePort === localPort) {
          // Message is from source
          setReceivedSourceMessages((prevMessages) => [...prevMessages, data.content]);
        } else {
          // Message is from target
          setReceivedTargetMessages((prevMessages) => [...prevMessages, data.content]);
        }
      } else if (data.type === 'packetLoss') {
        setStatistics((prevStats) => ({ ...prevStats, lost: prevStats.lost + 1 }));
      }
    });

    return () => {
      socket.close();
    };
  }, [socket, localPort]);

  return (
    <div>
      <h2>Source Box (Local Port: {localPort})</h2>
      <div>
        <input type="text" value={sourceMessage} onChange={(e) => setSourceMessage(e.target.value)} />
        <button onClick={() => sendSourceMessage()}>Send Message</button>
      </div>
      <div>
        <h3>Sent Messages:</h3>
        <ul>
          {receivedSourceMessages.map((msg, index) => (
            <li key={index}>{msg}</li>
          ))}
        </ul>
      </div>
      <h2>Target Box</h2>
      <div>
        <input type="text" value={targetMessage} onChange={(e) => setTargetMessage(e.target.value)} />
        <button onClick={() => sendTargetMessage()}>Send Message</button>
      </div>
      <div>
        <h3>Sent Messages:</h3>
        <ul>
          {receivedTargetMessages.map((msg, index) => (
            <li key={index}>{msg}</li>
          ))}
        </ul>
      </div>
      <div>
        <h3>Statistics:</h3>
        <p>Source Sent: {statistics.sourceSent}</p>
        <p>Target Sent: {statistics.targetSent}</p>
        <p>Received: {statistics.received}</p>
        <p>Lost: {statistics.lost}</p>
      </div>
    </div>
  );

  function sendSourceMessage() {
    const packetLoss = Math.random() < 0.2;
    const data = JSON.stringify({
      type: 'message',
      content: sourceMessage,
      packetLoss,
      sourcePort: localPort,
      targetPort: 50002, // Replace with your target port
    });
    socket.send(data);
    setStatistics((prevStats) => ({ ...prevStats, sourceSent: prevStats.sourceSent + 1 }));
    setSourceMessage('');
  }

  function sendTargetMessage() {
    const packetLoss = Math.random() < 0.2;
    const data = JSON.stringify({
      type: 'message',
      content: targetMessage,
      packetLoss,
      sourcePort: localPort,
      targetPort: 50001, // Replace with your source port
    });
    socket.send(data);
    setStatistics((prevStats) => ({ ...prevStats, targetSent: prevStats.targetSent + 1 }));
    setTargetMessage('');
  }
};

export default UDPComponent;
