import React, { useState, useEffect } from 'react';

const UDPComponent = ({ localPort, direction, sharedWebSocket }) => {
  const [messages, setMessages] = useState([]);
  const [loss, setLoss] = useState(0.2); // Default loss
  const [delay, setDelay] = useState(0); // Default delay

  useEffect(() => {
    const handleWebSocketMessage = (event) => {
      console.log('Received WebSocket message:', event.data);
      const data = JSON.parse(event.data);

      if (data.type === 'message' && data.direction === direction) {
        setMessages((prevMessages) => [...prevMessages, data.content]);
      }
    };

    sharedWebSocket.addEventListener('message', handleWebSocketMessage);

    return () => {
      sharedWebSocket.removeEventListener('message', handleWebSocketMessage);
    };
  }, [direction, sharedWebSocket]);

  useEffect(() => {
    const sendSettings = () => {
      if (sharedWebSocket.readyState === WebSocket.OPEN) {
        const settings = {
          type: 'settings',
          direction,
          loss,
          delay,
        };
        sharedWebSocket.send(JSON.stringify(settings));
      } else if (sharedWebSocket.readyState === WebSocket.CONNECTING) {
        // If the WebSocket is still connecting, wait and retry
        setTimeout(sendSettings, 100);
      } else {
        console.error('WebSocket connection not open');
      }
    };
  
    sendSettings(); // Send settings on initial render
  
    // Send settings whenever they change
    const timeoutId = setTimeout(() => {
      sendSettings();
    }, 500);
  
    return () => clearTimeout(timeoutId);
  }, [loss, delay, direction, sharedWebSocket]);

  return (
    <div>
      <h2>{direction === 'source' ? 'Source' : 'Target'} Box (Local Port: {localPort})</h2>
      <div>
        <h3>Received Messages:</h3>
        <ul>
          {messages.map((msg, index) => (
            <li key={index}>{msg}</li>
          ))}
        </ul>
      </div>
      <div>
        <h3>Settings:</h3>
        <label>
          Loss:
          <input type="number" value={loss} onChange={(e) => setLoss(parseFloat(e.target.value))} />
        </label>
        <label>
          Delay (ms):
          <input type="number" value={delay} onChange={(e) => setDelay(parseInt(e.target.value))} />
        </label>
      </div>
    </div>
  );
};

export default UDPComponent;
