import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UDPComponent = ({ localPort, direction }) => {
  const [messages, setMessages] = useState([]);
  const [loss, setLoss] = useState(0.2); // Default loss
  const [delay, setDelay] = useState(0); // Default delay

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch data from the server
        const response = await axios.get(`http://localhost:3000/getData?direction=${direction}`);
        const data = response.data;

        // Update messages state
        if (data.type === 'message' && data.direction === direction) {
          setMessages((prevMessages) => [...prevMessages, data.content]);
        }
      } catch (error) {
        console.error('Error fetching data from server:', error);
      }
    };

    fetchData(); // Fetch data on initial render

    // Fetch data periodically
    const intervalId = setInterval(() => {
      fetchData();
    }, 500);

    return () => clearInterval(intervalId);
  }, [direction]);

  const sendSettings = async () => {
    try {
      // Send settings to the server
      await axios.post('http://localhost:3000/updateSettings', {
        direction,
        loss,
        delay,
      });
      console.log('Settings sent successfully');
    } catch (error) {
      console.error('Error sending settings to server:', error);
    }
  };

  useEffect(() => {
    sendSettings(); // Send settings on initial render

    // Send settings whenever they change
    const timeoutId = setTimeout(() => {
      sendSettings();
    }, 500);

    return () => clearTimeout(timeoutId);
  }, [loss, delay, direction]);

  return (
    <div>
      <h2>{direction === 'source' ? 'Source' : 'Target'} Box (Local Port: {localPort})</h2>
      <div>
        <h3>Received Messages:</h3>
        <ul>
          {messages.map((msg, index) => (
            <li key={index}>{msg}</li>
          ))}
        </ul>
      </div>
      <div>
        <h3>Settings:</h3>
        <label>
          Loss:
          <input type="number" value={loss} onChange={(e) => setLoss(parseFloat(e.target.value))} />
        </label>
        <label>
          Delay (ms):
          <input type="number" value={delay} onChange={(e) => setDelay(parseInt(e.target.value))} />
        </label>
      </div>
    </div>
  );
};

export default UDPComponent;
