// App.js
import React, { useState, useEffect } from 'react';
import UDPComponent from './Components/UDPComponent';

const App = () => {
  const [targetIP, setTargetIP] = useState('127.0.0.1');
  const [targetPort, setTargetPort] = useState(60000);
  const [sourcePort, setSourcePort] = useState(60001);

  const [sharedWebSocket, setSharedWebSocket] = useState(null);

  useEffect(() => {
    const socket = new WebSocket(`ws://${targetIP}:${targetPort}`);
    setSharedWebSocket(socket);

    return () => {
      socket.close();
    };
  }, [targetIP, targetPort]);

  return (
    <div>
      <div>
        <label>
          Target IP:
          <input type="text" value={targetIP} onChange={(e) => setTargetIP(e.target.value)} />
        </label>
        <label>
          Target Port:
          <input type="number" value={targetPort} onChange={(e) => setTargetPort(e.target.value)} />
        </label>
        <label>
          Source Port:
          <input type="number" value={sourcePort} onChange={(e) => setSourcePort(e.target.value)} />
        </label>
      </div>
      {sharedWebSocket && <UDPComponent localPort={sourcePort} direction="source" sharedWebSocket={sharedWebSocket} />}
      {sharedWebSocket && <UDPComponent localPort={targetPort} direction="target" sharedWebSocket={sharedWebSocket} />}
    </div>
  );
};

export default App;
